// import pizzaData from "./data"

const pizzaData = [
    {
        name: "Focaccia",
        ingredients: "Bread with italian olive oil and rosemary",
        price: 6,
        photoName: "pizzas/focaccia.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Margherita",
        ingredients: "Tomato and mozarella",
        price: 10,
        photoName: "pizzas/margherita.jpg",
        soldOut: true,
    },
    {
        name: "Pizza Spinaci",
        ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
        price: 12,
        photoName: "pizzas/spinaci.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Funghi",
        ingredients: "Tomato, mozarella, mushrooms, and onion",
        price: 12,
        photoName: "pizzas/funghi.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Salamino",
        ingredients: "Tomato, mozarella, and pepperoni",
        price: 15,
        photoName: "pizzas/salamino.jpg",
        soldOut: true,
    },
    {
        name: "Pizza Prosciutto",
        ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
        price: 18,
        photoName: "pizzas/prosciutto.jpg",
        soldOut: false,
    },
];

export default function App() {

    return (
        <div>
            <h1>Pizza Menu</h1>
            <Header />
            <Menu />
            <Footer />
        </div >
    )
}

function Header() {
    const styling = { color: '#FF00AE', backgroundColor: 'red' }
    return <p style={styling}>Header</p>
}

function Menu() {
    return <>
        {pizzaData.map((pizza, i) => <Pizza key={i} pizza={pizza} />)}
    </>
}

function Footer() {
    const hour = new Date().getHours();
    const openHour = 10;
    const closeHour = 22;
    const isOpen = hour >= openHour && hour <= closeHour;

    return <footer>
        {isOpen ? `We are open until ${closeHour}, come visit us` : `We are closed now, please visit us at ${openHour}`}
    </footer>
}

function Pizza({ pizza, i }) {
    const { photoName, name, ingredients, price } = pizza;
    return <div>
        <span>{i}</span>
        <img src={photoName} alt={name} />
        <h2>{name}</h2>
        <p>{ingredients}</p>
        <p>{price}</p>
    </div>
}

