import { useEffect, useState } from "react";

function App() {
  const [advice, setAdvice] = useState('');
  const [count, setCount] = useState(0)
  const getAdviceSlip = async function () {
    const res = await fetch('https://api.adviceslip.com/advice');
    const data = await res.json();
    setAdvice(data.slip.advice);
    setCount(count + 1)
  }

  // useEffect(() => {
  //   getAdviceSlip()
  // }, [0])

  return (
    <div>
      <h2>{advice}</h2>
      <Greet count={count} />
      <button onClick={getAdviceSlip}>Click me!</button>
    </div>
  );
}

function Greet(props) {
  return <p>you get adviced {props.count} times</p>
}

export default App;
