  const getAdviceSlip = async function () {
    const res = await fetch('https://api.adviceslip.com/advice');
    const data = await res.json();
    console.log(data.slip.advice);
  }
  return (
    <div>
      <h2>hello world</h2>
      <button onClick={getAdviceSlip}>Click me!</button>
    </div>
  );
}

export default App;
