import React from "react";
import ReactDOM from "react-dom/client";
import './index.css';

const pizzaData = [
    {
        name: "Focaccia",
        ingredients: "Bread with italian olive oil and rosemary",
        price: 6,
        photoName: "pizzas/focaccia.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Margherita",
        ingredients: "Tomato and mozarella",
        price: 10,
        photoName: "pizzas/margherita.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Spinaci",
        ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
        price: 12,
        photoName: "pizzas/spinaci.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Funghi",
        ingredients: "Tomato, mozarella, mushrooms, and onion",
        price: 12,
        photoName: "pizzas/funghi.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Salamino",
        ingredients: "Tomato, mozarella, and pepperoni",
        price: 15,
        photoName: "pizzas/salamino.jpg",
        soldOut: true,
    },
    {
        name: "Pizza Prosciutto",
        ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
        price: 18,
        photoName: "pizzas/prosciutto.jpg",
        soldOut: false,
    },
];

function App() {
    return (
        <div>
            <Header />
            <Menu />
            <Footer />
        </div>
    )
}

function Header() {
    const style = { color: 'green', fontSize: '40px', textTransform: 'uppercase' };
    return <h1 style={style}>Fast Pizza Delivery Co.</h1>
}

function Menu() {
    return <div className="menu">
        <h2>Today's Menu</h2>
        <div className="menu-list">
            {pizzaData.map((pizza) => (
                // <Pizza name={pizza.name} ingredient={pizza.ingredients} price={pizza.price} photoName={pizza.photoName} />
                <Pizza pizzaObj={pizza} key={pizza.name} />
            ))}
            {/* <Pizza
                name="Pizza Corn"
                cost={20}
                ingredient="Tomato, mozarella, spinach and ricotta cheese"
                photoName="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTUOwj-9B9lOBTsPvHVFusqiAhHrvIlSB1dHg&usqp=CAU"
            />
            <Pizza
                name="Veg Pizza"
                cost={23}
                ingredient="Tomato, mozarella, spinach and ricotta cheese"
                photoName="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTUOwj-9B9lOBTsPvHVFusqiAhHrvIlSB1dHg&usqp=CAU"
            />
            <Pizza
                name="Pizza Moririlla"
                cost={30}
                ingredient="Tomato, mozarella, spinach and ricotta cheese"
                photoName="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTUOwj-9B9lOBTsPvHVFusqiAhHrvIlSB1dHg&usqp=CAU"
            /> */}
        </div>
    </div>
}

function Pizza(props) {
    return (
        <div className="item">
            <div className="item-photo">
                <img src={props.pizzaObj.photoName} alt={props.pizzaObj.name} />
            </div>
            <div className="item-details">
                <h3>{props.pizzaObj.name}</h3>
                <p>{props.pizzaObj.ingredients}</p>
                <span>{props.pizzaObj.price}</span>
            </div>
        </div>
    )
}

function Footer() {
    const hour = new Date().getHours();
    const openHour = 10;
    const closedHour = 22;
    const isOpen = hour >= openHour && hour <= closedHour;
    console.log(isOpen);
    return (
        <div>
            <footer>{isOpen && <p>We're open until {closedHour}:00. Come visit us or order online</p>}</footer>
            <button className="order-btn">Order now</button>
        </div>
    )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<React.StrictMode><App /></React.StrictMode>)
