// import pizzaData from "./data"

export default function App() {

    return (
        <div>

            {/* <Headers />
            <Menu />
            <Footers /> */}
        </div >
    )
}

function Menu() {
    const pizzaNumb = pizzaData.length;
    return <>
        <h2>Our Menu</h2>
        {pizzaNumb > 0 ? <div className="pizza-item">
            {pizzaData.map((pizza) => (
                <Pizza pizzaObj={pizza} key={pizza.name} />
            ))}
        </div> : <p>Oops no items available, check back later :)</p>
        }</>
}

function Pizza({ pizzaObj }) {
    const { name, photoName, ingredients, price, soldOut } = pizzaObj;

    return <div className={`pizza-list ${soldOut ? 'sold-out' : 'on-sell'} `}>
        <img src={photoName} alt={name} />
        <h2>{name}</h2>
        <p>{ingredients}</p>
        <p>{soldOut ? 'Sold out' : price}</p>
    </div >
}

function Headers() {
    const pizzaTitle = pizzaData;
    const styling = { color: "#fff", backgroundColor: "green" }
    return <div className="main-header">
        <h1 style={styling}>Fast Pizza Company</h1>
        {pizzaTitle.length > 0 && <>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. <br />Lorem Ipsum has been the industry's standard dummy text ever since the 1500s,</p></>}
    </div>
}

function Footers() {
    const hours = new Date().getHours();
    const openHour = 12;
    const closeHour = 22;
    const isOpen = hours >= openHour && hours <= closeHour;
    return <div className="feeter">
        {isOpen ? <OpenFooter openedHour={openHour} closedHour={closeHour} /> :
            <p>We are happy to welcome you between {openHour}:00 and {closeHour}:00</p>}
    </div>
}

function OpenFooter({ openedHour, closedHour }) {
    return <>
        <p>{new Date().toLocaleTimeString()}. We're open from {openedHour}:00 to {closedHour}:00 Come visit us or order online</p>
        <button>Order Now</button></>
}
