import React from "react";
import ReactDOM from "react-dom/client";

function App() {
    return (
        <div>
            <Header />
            <Menu />
            <Footer />
        </div>
    )
}

function Header() {
    const style = { color: 'green', textTransform: 'uppercase' }
    return <h1 style={style}>Fast Pizza Delivery Co.</h1>
}

function Menu() {
    return <div>
        <h2>Menu</h2>
        <Pizza />
    </div>
}

function Footer() {
    const hour = new Date().getHours();
    const open = hour >= 10 && hour <= 22 ? 'We are currently open' : 'Sorry, We are closed'

    return (
        <div>
            <footer>{new Date().toLocaleTimeString()}, {open}</footer>
        </div>
    )
}

function Pizza() {
    return (
        <div>
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTUOwj-9B9lOBTsPvHVFusqiAhHrvIlSB1dHg&usqp=CAU" alt="Pizza" />
            <h3>Biryani</h3>
            <p>Its delicious</p>
        </div>
    )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<React.StrictMode><App /></React.StrictMode>)
