const curINR = <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 320 512"><path d="M0 64C0 46.3 14.3 32 32 32H96h16H288c17.7 0 32 14.3 32 32s-14.3 32-32 32H231.8c9.6 14.4 16.7 30.6 20.7 48H288c17.7 0 32 14.3 32 32s-14.3 32-32 32H252.4c-13.2 58.3-61.9 103.2-122.2 110.9L274.6 422c14.4 10.3 17.7 30.3 7.4 44.6s-30.3 17.7-44.6 7.4L13.4 314C2.1 306-2.7 291.5 1.5 278.2S18.1 256 32 256h80c32.8 0 61-19.7 73.3-48H32c-17.7 0-32-14.3-32-32s14.3-32 32-32H185.3C173 115.7 144.8 96 112 96H96 32C14.3 96 0 81.7 0 64z" /></svg>;
function Pizza({ pizzaObj }) {
    const { name, ingredients, price, photoName, soldOut } = pizzaObj;
    return (
        <div className={`item ${soldOut ? 'sold-out' : ''}`}>
            <div className="item-photo">
                <img src={photoName} alt={name} />
            </div>
            <div className="item-details">
                <h3>{name}</h3>
                <p>{ingredients}</p>
                {soldOut ? (<span>SOLD OUT</span>) : (<span>{curINR} {price}</span>)}
            </div>
        </div>
    )
}

export default Pizza;
