import React from "react";
import ReactDOM from "react-dom/client";
import './index.css';

const curINR = <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 320 512"><path d="M0 64C0 46.3 14.3 32 32 32H96h16H288c17.7 0 32 14.3 32 32s-14.3 32-32 32H231.8c9.6 14.4 16.7 30.6 20.7 48H288c17.7 0 32 14.3 32 32s-14.3 32-32 32H252.4c-13.2 58.3-61.9 103.2-122.2 110.9L274.6 422c14.4 10.3 17.7 30.3 7.4 44.6s-30.3 17.7-44.6 7.4L13.4 314C2.1 306-2.7 291.5 1.5 278.2S18.1 256 32 256h80c32.8 0 61-19.7 73.3-48H32c-17.7 0-32-14.3-32-32s14.3-32 32-32H185.3C173 115.7 144.8 96 112 96H96 32C14.3 96 0 81.7 0 64z" /></svg>;

const pizzaData = [
    {
        name: "Focaccia",
        ingredients: "Bread with italian olive oil and rosemary",
        price: 6,
        photoName: "pizzas/focaccia.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Margherita",
        ingredients: "Tomato and mozarella",
        price: 10,
        photoName: "pizzas/margherita.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Spinaci",
        ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
        price: 12,
        photoName: "pizzas/spinaci.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Funghi",
        ingredients: "Tomato, mozarella, mushrooms, and onion",
        price: 12,
        photoName: "pizzas/funghi.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Salamino",
        ingredients: "Tomato, mozarella, and pepperoni",
        price: 15,
        photoName: "pizzas/salamino.jpg",
        soldOut: true,
    },
    {
        name: "Pizza Prosciutto",
        ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
        price: 18,
        photoName: "pizzas/prosciutto.jpg",
        soldOut: false,
    },
];

function App() {
    return (
        <div>
            <Header />
            <Menu />
            <Footer />
        </div>
    )
}

function Header() {
    const style = { color: 'green', fontSize: '40px', textTransform: 'uppercase' };
    const pizzaNum = [pizzaData];
    return <div className="header">
        <h1 style={style}>Fast Pizza Delivery Co.</h1>
        {pizzaNum.length > 0 && <HeaderMsg />}

    </div>
}

function HeaderMsg() {
    return (
        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s,</p>
    )
}

function Menu() {
    const pizzas = pizzaData;
    const pizzaNumb = pizzas.length;
    return <div className="menu">
        <h2>Today's Menu</h2>
        {pizzaNumb > 0 ? (
            <div className="menu-list">
                {pizzas.map((pizza) => (
                    <Pizza pizzaObj={pizza} key={pizza.name} />
                ))}
            </div>
        ) : <p>Oops no items available, check back later :)</p>}
    </div>
}

function Pizza({ pizzaObj }) {
    console.log(pizzaObj);
    if (pizzaObj.soldOut)
        return (
            <div className="item sold-out">
                <div className="item-photo">
                    <img src={pizzaObj.photoName} alt={pizzaObj.name} />
                </div>
                <div className="item-details">
                    <h3>{pizzaObj.name}</h3>
                    <p>{pizzaObj.ingredients}</p>
                    <span>{curINR} {pizzaObj.price}</span>
                </div>
            </div>
        )


    return (
        <div className="item">
            <div className="item-photo">
                <img src={pizzaObj.photoName} alt={pizzaObj.name} />
            </div>
            <div className="item-details">
                <h3>{pizzaObj.name}</h3>
                <p>{pizzaObj.ingredients}</p>
                <span>{curINR} {pizzaObj.price}</span>
            </div>
        </div>
    )
}

function Footer() {
    const hour = new Date().getHours();
    const openHour = 10;
    const closedHour = 22;
    const isOpen = hour >= openHour && hour <= closedHour;

    return (
        <footer className="footer">
            {isOpen ? (
                <FootData closedHour={closedHour} openHour={openHour} />
            )
                : (<div><p>We're happy to welcome you between {openHour}:00 and {closedHour}:00</p></div>)}
        </footer >
    )
}

function FootData({ closedHour, openHour }) {
    return (
        <div className="footerTxt">
            <p>We're open from {openHour}:00 to {closedHour}:00. Come visit us or order online</p>
            <button className="order-btn">Order now</button>
        </div>
    )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<React.StrictMode><App /></React.StrictMode>)
