import pizzaData from "./data"

export default function App() {
    const pizzaNumb = pizzaData.length;
    return (
        <div>
            <Headers />
            {pizzaNumb > 0 ? <div className="pizza-item">
                {pizzaData.map((pizza) => (
                    <Pizza pizza={pizza} key={pizza.name} />
                ))}
            </div> : <p>Oops no items available, check back later :)</p>
            }
            <Footers />
        </div >
    )
}

function Menu() {

}

function Pizza({ pizza }) {
    const { name, photoName, ingredients, price, soldOut } = pizza;
    return <div className={`pizza-list ${soldOut ? 'sold-out' : ''} `}>
        <img src={photoName} alt={name} />
        <h2>{name}</h2>
        <p>{ingredients}</p>
        <p>{price}</p>
    </div>
}

function Headers() {
    const pizzaTitle = pizzaData;
    return <div className="main-header">
        <h1>Fast Pizza Company</h1>
        {pizzaTitle.length > 0 && <><h2>Our Menu</h2>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. <br />Lorem Ipsum has been the industry's standard dummy text ever since the 1500s,</p></>}
    </div>
}

function Footers() {
    const hours = new Date().getHours();
    return <div className="feeter">
        {hours >= 10 && hours <= 22 ? <><p>{new Date().toLocaleTimeString()}. We're open until 22:00. Come visit us or order online</p>
            <a href="#">Order Now</a></> : <p>We're closed now.</p>}
    </div>
}
