import pizzaData from "./data"

export default function App() {

    return (
        <div>
            <h1>Pizza Menu</h1>
            <Header />
            <Menu />
            <Footer />
        </div >
    )
}

function Header() {
    const styling = { color: '#FF00AE', backgroundColor: 'red' }
    return <p style={styling}>Header</p>
}

function Menu() {
    return <>
        {pizzaData.map((pizza, i) => <Pizza key={i} pizza={pizza} />)}
    </>
}

function Footer() {
    const hour = new Date().getHours();
    const openHour = 10;
    const closeHour = 22;
    const isOpen = hour >= openHour && hour <= closeHour;

    return <footer>
        {isOpen ? <Order close={closeHour} /> :
            <p>We are closed now, please visit us at {openHour}:00</p>}
    </footer>
}

function Order(props) {
    return <p>We are open until {props.close}:00, come visit us</p>
}

function Pizza({ pizza, i }) {
    const { photoName, name, ingredients, price } = pizza;
    return <div>
        <span>{i}</span>
        <img src={photoName} alt={name} />
        <h2>{name}</h2>
        <p>{ingredients}</p>
        <p>{price}</p>
    </div>
}

