import React from "react";
import ReactDOM from "react-dom/client";
import './index.css';

function App() {
    return (
        <div>
            <Header />
            <Menu />
            <Footer />
        </div>
    )
}

function Header() {
    const style = { color: 'green', fontSize: '40px', textTransform: 'uppercase' };
    return <h1 style={style}>Fast Pizza Delivery Co.</h1>
}

function Menu() {
    return <div>
        <h2>Menu</h2>
        <Pizza name="Pizza Corn" cost="20" ingredient="Tomato, mozarella, spinach and ricotta cheese" photoName="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTUOwj-9B9lOBTsPvHVFusqiAhHrvIlSB1dHg&usqp=CAU" />
    </div>
}

function Pizza(props) {
    return (
        <div>
            <img src={props.photoName} alt={props.name} />
            <h3>{props.name}</h3>
            <p>{props.ingredient}</p>
        </div>
    )
}

function Footer() {
    const hour = new Date().getHours();
    const open = hour >= 10 && hour <= 22 ? 'We are currently open' : 'Sorry, We are closed'

    return (
        <div>
            <footer>{new Date().toLocaleTimeString()}, {open}</footer>
        </div>
    )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<React.StrictMode><App /></React.StrictMode>)
